sys_script_include.script:
var GetRequestedEmailAddress = Class.create();
GetRequestedEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmailAddress: function(){
		var gr = new GlideRecord("sys_user");
		gr.get(this.getParameter('sysparm_userID'));
		return gr.email + '';

	},

	type: 'GetRequestedEmailAddress'
});
